import React from "react";
import './General.scss'

export const General = () => {
    const [display, setDisplay] = React.useState({student:true, group:true})
    // const [group, setGroup] = React.useState(true)



  function checkClick(event){
      if (event.target.className === 'blue'){
          setDisplay({student:true, group:false})
      }else if(event.target.className === 'red'){
          setDisplay({student:false, group:true})
      }
  }


    return(

        <div className="general__container">
        <div className="general__info">

            {/* Check the possibility of packing all in one function */}
            <div className="general__container__content">
            <div className = "general__container__content-title">
            <p>Мои ученики</p>
            </div>

            <div className="general__container__content-subtitle">
                <p className='blue' onClick={checkClick} >Ученики</p>
                <p className='red' onClick={checkClick}>Группы</p>
            </div>
            <div className="general__container__content-borderline"></div>
            <div className="general__container__content-underline">
            {display.student ? <div className="general__container__content-underline-blue"></div> : <div className="general__container__content-underline-blue" style={{backgroundColor:'white'}}></div>}
            {display.group && <div className="general__container__content-underline-red"></div>}
            </div>

            </div>







            {/* It ends here */}

        </div>
        </div>

    )
}
