import React from 'react'
import { TableHead } from './TableHead'
import {TableRow} from './TableRow'
import './Table.scss'
// import { studentData } from '../studentData'



export const Table = ({theadData,schedule,displayLessonDetail,choice}) =>{

  console.log('component has rerendered')

    return(

        <table className="main__table" cellspacing="0" >
            <thead className="main__table__heading">
                <tr className="main__table__heading__row">
                    {theadData.map(head=><TableHead key={head} item={head}></TableHead>)}
                </tr>
            </thead>
            <tbody className="main__table__body">
                {Object.keys(schedule[0]).length ? schedule[0].map((item)=><TableRow choice={choice} key={schedule.id} data={item.items} value={schedule[0]} displayLessonDetail={()=>displayLessonDetail(schedule)}></TableRow>) : <div className="table__loading"><h1 >Loading...</h1></div>}
            </tbody>

        </table>
    )
}


