                <p className='blue' onClick={checkClick} ref = {buttonRef}>Ученики</p>
                <p className='red' onClick={checkClick}>Группы</p>
            </div>
            <div className="general__container__content-borderline"></div>
            <div className="general__container__content-underline"></div>

            </div>







