import { TableHead } from './TableHead'
import {TableRow} from './TableRow'
import './Table.scss'

export const Table = ({theadData,selectedData,person}) =>{
    console.log('table is rerendering')
    console.log(selectedData)
    const [state,setState] = React.useState([])
    // let newData=[]
    // newData = [...selectedData]

    setInterval(()=>{
        setState(selectedData)
    })

    function handleData(){
        let data=[]
        data=selectedData
        // for (let val in selectedData){
        //     data.push(val)
        // }

        return data
    }



    return(

        <table className="main__table" cellspacing="0" >
            <thead className="main__table__heading">
                <tr className="main__table__heading__row">
                    {theadData.map(head=><TableHead key={head} item={head}></TableHead>)}
                </tr>
            </thead>
            <tbody className="main__table__body">
                {handleData()?.map((item)=><TableRow key={item.id} data={item.items}></TableRow>)}
            </tbody>

        </table>
    )
}
