import React from 'react'
import { TableHead } from './TableHead'
import {TableRow} from './TableRow'
import './Table.scss'



export const Table = ({theadData,selectedData,person}) =>{

     const [state, setState] = React.useState([])
       React.useEffect(()=>{
      console.log('effect')
      setState(prev=>[selectedData[person]])
  },[person])

    return(

        <table className="main__table" cellspacing="0" >
            <thead className="main__table__heading">
                <tr className="main__table__heading__row">
                    {theadData.map(head=><TableHead key={head} item={head}></TableHead>)}
                </tr>
            </thead>
            <tbody className="main__table__body">
                {state && state.map((item)=><TableRow key={item.id} data={item.items}></TableRow>)}
            </tbody>

        </table>
    )
}
