import React from 'react'

export const TableRow = ({data,choice,displayLessonDetail}) =>{


    // function rowClick(){
    //     console.log('table row clicked')

    //     console.log(value)
    // }





    return(
        <tr className="main__table__row" >
            { data.map((item)=>{

                return <td key={item} className="main__table__row__box" onClick={choice=== "grades" ? displayLessonDetail : ()=>{}}>
                    <div className="main__table__row__box__single">
                    {item}
                    </div>
                    </td>
            })} 
        </tr>

    )
}
